import React from "react";

const SortButton = ({ascState}) => {

    const hundleSortClick = (bool)=>{
        ascState = bool;
    };
    
    return (
        <div className='sort-button'>
            <button onClick={hundleClick(true)}>昇順</button>
            <button onClick={hundleClick(false)}>降順</button>
        </div>
    )
};




export default SortButton;
