//関数コンポーネントVer
function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}


//================================================================
//================================================================


class Board extends React.Component {

  renderSquare(i) {
    return <
      Square
      value={this.props.squares[i]}
      onClick={() => { this.props.onClick(i) }}
    />;
  }


  render() {
    return (
      <div>
        {
          Array(3).fill(0).map((v, i) => {
            return (
              <div className="board-row" key={i}>
                {
                  Array(3).fill(0).map((v, j) => {
                    console.log(i, j,(i+1)*(j+1));
                    // 0 - 8
                    // [0,1,2]
                    return this.renderSquare(i)
                  })
                }
              </div>
            )
          })
        }
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [{
        squares: Array(9).fill(null),
        place: 0,
      }],
      xIsNext: true,
      stepNumber: 0,
    }
  }

  hundleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = [...current.squares];
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'x' : 'o';
    this.setState({
      history: history.concat([{
        squares: squares,
        place: i,
      }]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext,
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step % 2) === 0,
    });
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);
    const moves = history.map((step, move) => {
      const col = Math.floor(step.place / 3) + 1;
      const row = step.place % 3 + 1;
      const desc = move ?
        `Go to move #${move} | player:${move % 2 === 1 ? 'x' : 'o'} put on (${col},${row})` : 'Go to game start';

      return (
        <li key={move} >
          <button className={this.state.stepNumber === move ? 'selected' : ''} onClick={() => { this.jumpTo(move) }}>{desc}</button>
        </li>
      );


    })
    let status;
    if (winner) {
      status = `Winner: ${winner}`;
    } else {
      status = `Next player is ${this.state.xIsNext ? 'x' : 'o'}`;
    }

    return (
      <div>
        <p>Selected at {this.state.stepNumber}</p>
        <div className="game">
          <div className="game-board">
            <Board
              squares={current.squares}
              onClick={(i) => { this.hundleClick(i) }}
            />
          </div>
          <div className="game-info">
            <div>{status}</div>
            <ol className="game-history">{moves}</ol>
          </div>
        </div>
      </div>
    );
  }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
